$(document).ready(function(){
  
  //Toggle Navigation
  let icon = true;
  $('.nav-menu').click(function(event){
    $('.menu-content').toggle(500);
    if(icon == true){
      $('#icon-menu').attr('class','fa-solid fa-xmark');
      icon = false;
    }else{
      $('#icon-menu').attr('class','fa-solid fa-bars');
      icon = true;
    }
  });
  
});